def isValid(s):
    S=list(set(s))
    count=s.count(S[0])
    flag=0
    for i in range(1,len(S)):
        if s.count(S[i])!=count:
            if s.count(S[i])-1==count:
                flag+=1
                continue
            if s.count(S[i])-1==0:
                flag+=1
                continue
            else:
                return 'NO'
            
    if flag<=1:
        return 'YES'
    else:
        return 'NO'
s=''
print(isValid(s))
